import React, { useState, useEffect } from 'react'
import moviesData from "../../api/movies"
import Modal from "../../components/Modal/Modal"
import "./Movie.css"
import { useParams } from "react-router"


export default function Movie() {
    // route ==> /movie/:id

    let { id } = useParams();
    const [movie, setMovie] = useState();
    const [isOpen, setIsOpen] = useState(false);

    useEffect(() => {
        const getMovie = moviesData.find(i => i.id === id);
        setMovie(getMovie);
    }, [id]);



    return movie ? (
        <div className="Movie" style={{}}>
            <h2>{movie.title}</h2>
            <p>{movie.description}</p>
            <div onClick={() => setIsOpen(true)}
            className="Play"
            data-bs-toggle="modal"
            data-bs-target="#exampleModal"            
            >
              Play 
            </div>

<Modal isOpen={isOpen} setIsOpen={setIsOpen}>
    <div className="modal-body">
        <iframe width="560" height="315" src={movie.trailer} frameBorder="0" allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture" allowFullScreen></iframe>&#x1F947;
    </div>
</Modal>
        </div>
    ) : (
        <div>Loading...</div>
    )
}
